import React from 'react';
import Vivus from 'vivus';
import { Image} from 'react-bootstrap';
import { Link } from 'react-router-dom';
import '../../src/app.css';


import Compass from "../resources/icons/svg/Compass.svg";
import Pawprint from "../resources/icons/svg/Pawprint.svg";
import BookVivus from "../resources/icons/vivus/Book.js";
import Team_animated from "../resources/icons/svg/Team.svg";
import Contact from "../resources/icons/svg/Contact.svg";

import CompassButton from '../resources/icons/svgr/CompassButton/index.jsx';
import PawprintButton from '../resources/icons/svgr/PawprintButton/index.jsx';

import Icon from '../resources/icons/js/FbButton.js';

class Sidebar extends React.Component{
  constructor(){
    super();
    this.state={
        isActive:false
    }
}

play = () =>{
    const {isActive} = this.state;
    this.setState(prevState => ({isActive: !prevState.isActive}));

};
    

    render(){
      const{isActive} = this.state;
        return(
        <div className="main-sidebar">
            
            <Link to="/feed"><CompassButton /></Link>
            <Link to="/projects"><PawprintButton  play={this.play} isActive={isActive} onClick={this.play}/></Link>
            
            <Link to="/team"><div className="sidebar-button">{<div id="Team" width="100"/>}</div></Link>
            <Link to="/contact"><div className="sidebar-button"><Icon width={50} fill="none"/></div></Link>
        </div>
        );
    }   
    
    
}

export default Sidebar;
