import React from 'react';
import {Row, Col, Container, Card, ListGroup, Button, Image} from 'react-bootstrap';
import './app.css';
import './custom.scss';

import MainSpace from"./components/mainspace.js";
import Sidebar from "./components/sidebar.js";

class XMApp extends React.Component {
  render (){
    let width = window.screen.width;
    if(width>768){
      return(
        <Container fluid>
          <Row className='base'>
            <Sidebar/>
            <MainSpace/>
          </Row>
        </Container>
      );
    }
    else{
      return(
        <Container fluid>
          <Row className='base'>
            <MainSpace/>
            <Sidebar/>
          </Row>
        </Container>
      );
    }
  }
}


export default XMApp;
