import React from 'react';
import styled from 'styled-components';
import {ReactComponent as svg1} from '../../svg/Compass.svg';
import {ReactComponent as svg2} from '../../svg/Compass.svg';

const ToggleWrapper = styled.span`
    cursor: pointer;
    display: inline-block;
    height: 5rem;
    position: relative;
    width: 5rem;

    /*svg animations*/
    @keyframes draw_in {
        50% { stroke-dashoffset: 100%; }
        100% { stroke-dashoffset: 0%; }
      }
      @keyframes draw_out {
        50% { stroke-dashoffset: 0%; }
        100% { stroke-dashoffset: 100%; }
      }
      @keyframes rotate_in_ccw {
        0% { transform: rotate(0deg) }
        100% { transform: rotate(180deg) }
      }

    .svg1{
        .compass_needle{
            animation:$rotate_in_ccw 0.8s;
            animation-fill-mode: forwards;
        } 
    }

    svg{
        circle, line, polygon, path{
            stroke: {props => props.status ==='open' ? '#2C6975' : 'black'};
            stroke-linecap: round;
            stroke-miterlimit: 10;
            stroke-width: 5px;
        }
    }

`;
export const CompassButton = () => {
    const[iconStatus, setIconStatus] = React.useState('default');

    const toggle =()=>{
        iconStatus === 'default'
      ? setIconStatus('open')
      : setIconStatus('default');
    }

    return (
        <ToggleWrapper
          onClick={() => { toggle() }}
          status={iconStatus}
        >
          <svg1 className='svg-1' />
          <svg2 className='svg-2' />
        </ToggleWrapper>
      );
}
