import React from 'react';
import {Container, Row, Col} from 'react-bootstrap';
import '../../src/app.css';
import CompassButton from '../resources/icons/svgr/CompassButton/index.jsx';
class MainSpace extends React.Component {

    constructor(){
        super();
        this.state={
            isActive:false
        }
    }

    play = () =>{
        const {isActive} = this.state;
        this.setState(prevState => ({isActive: !prevState.isActive}));

    };

    render (){
        const{isActive} = this.state;
        return(
            <Col className="main-space">
                <Col className='main-header'>XM Studio | {this.props.heading}
                <Col><CompassButton onClick={this.play} isActive={this.play}/></Col></Col>
                <Col className='main-content'></Col>
            </Col>     
        );
    }
}

export default MainSpace;
