import React from 'react';
import {Row, Col, Container, Card, ListGroup, Button, Image} from 'react-bootstrap';
import './app.css';
import './custom.scss';

import MainSpace from"./components/mainspace.js";
import Sidebar from "./components/sidebar.js";

class XMApp extends React.Component {
  constructor(props){
    super(props);
    let width = window.screen.width;
    this.state={};
    if(width>768){
      this.state.renderComponent=(
        <Container fluid>
          <Row className='base'>
            <Sidebar/>
            <MainSpace/>
          </Row>
        </Container>
      );
    }
    else{
      this.state.renderComponent=(
        <Container fluid>
          <Row className='base'>
            <MainSpace/>
            <Sidebar/>
          </Row>
        </Container>
      );
    }
  }
  render (){
    return this.state.renderComponent;
    
  }
}

export default XMApp;
