import React from 'react';
import {Row, Col, Container, Card, ListGroup, Button, Image} from 'react-bootstrap';
import './app.css';
import './custom.scss';

import MainSpace from"./components/mainspace.js";
import Sidebar from "./components/sidebar.js";

class XMFeed extends React.Component {
  render (){
    let width = window.screen.width;

    //PC Screen
    if(width>768){
      return(
        <Container fluid>
          <Row className='base'>
            <Sidebar/>
            <MainSpace heading="Feed"/>
          </Row>
        </Container>
      );
    }

    //Mobile Screen
    else{
      return(
        <Container fluid>
          <Row className='base'>
            <MainSpace heading="Feed"/>
            <Sidebar/>
          </Row>
        </Container>
      );
    }
  }
}


export default XMApp;
